import React from 'react'

export default function Menu({items}) {
  return (
    <div className="section-center">
        {items.map((menuItem)=>{
            const {id, title, img, desc} = menuItem;
            return <article key={id} className="menu-item"> 
            <img src={img} alt={title} className="photo"/>

            </article>
        })}


    </div>
  )
}
